const ExampleSlider = ({props}) => {
    const [show, setShow] = useState(false);
    const generalStore = useContext(StoreContext);


    useEffect(()=>{
        setShow(show);
    },[show])

    const handleSubmit = () => {
        setShow(!show);
        generalStore.setLayout(!show);

    }


    const counterClasses = ["slick-sliderrr"];
    if(show){
        counterClasses.push("active")
    }

    const settings = {
        dots: true,
        arrows: true,
        infinite: true,
        lazyLoad: true,
        fade: true,
        swipe: true,
        className: `${counterClasses.join("")}`,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
    };


    return <Slider
        {...settings}>
        {props.map(img => {
            return <div className="example" onClick={handleSubmit}>
                <div className="background"></div>
                <div className="pictureContainer">
                    <img src={img.src} alt="прозрачные роллеты" className="picture"/>
                </div>
                <div className="data">
                    <p>год: {img.year}</p>
                    <p>размеры: {img.size}</p>
                    <p>цвет поликарбоната: {img.colorP}</p>
                    <p>цвет металла: {img.colorM}</p>
                    <p>дополнительная комплектация: {img.addition}</p>
                </div>
            </div>
        })}
    </Slider>
}

